"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[811],{4212:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"/\ub9ac\uce58\ub9c8\uc791 \uc5ed \ub178\ub9bc \ubd84\ub958","metadata":{"permalink":"/mahjong-docs/\ub9ac\uce58\ub9c8\uc791 \uc5ed \ub178\ub9bc \ubd84\ub958","source":"@site/blog/\ub9ac\uce58\ub9c8\uc791 \uc5ed \ub178\ub9bc \ubd84\ub958.md","title":"\ub9ac\uce58\ub9c8\uc791 \uc5ed \ub178\ub9bc \ubd84\ub958","description":"\ud654\ub8cc\uc5d0 \ud544\uc694\ud55c \ub9ac\uce58\ub9c8\uc791\uc758 \uc5ed\ub4e4\uc744 \uc0c1\ud669\ubcc4\ub85c \ub178\ub9b4 \uc218 \uc788\ub3c4\ub85d \ubd84\ub958\ud558\uc5ec \ub098\uc5f4\ud568.","date":"2023-11-27T16:35:46.000Z","formattedDate":"November 27, 2023","tags":[],"readingTime":5.795,"hasTruncateMarker":false,"authors":[],"frontMatter":{},"unlisted":false},"content":":::note summary\\n\\n\ud654\ub8cc\uc5d0 \ud544\uc694\ud55c \ub9ac\uce58\ub9c8\uc791\uc758 \uc5ed\ub4e4\uc744 \uc0c1\ud669\ubcc4\ub85c \ub178\ub9b4 \uc218 \uc788\ub3c4\ub85d \ubd84\ub958\ud558\uc5ec \ub098\uc5f4\ud568.\\n:::\\n\\n:::warning\\n\\n\uc870\ud328\ub85c \ub178\ub9b4 \uc218 \uc5c6\uac70\ub098, \ub178\ub9ac\uae30 \uc5b4\ub824\uc6b4 \uc5ed\ub4e4\uc740 \uc77c\ubd80 \uc0dd\ub7b5\ud568.\\n:::\\n\\n## 0. \ubaa9\ucc28\\n---\\n\\n- [1. \uba58\uc820 \uc5ec\ubd80](#1-\uba58\uc820-\uc5ec\ubd80)\\n\\t- [1-1. \uba58\uc820](#1-1-\uba58\uc820)\\n\\t- [1-2. \ud6c4\ub85c](#1-2-\ud6c4\ub85c)\\n- [2. \ud328 \uc3e0\ub9bc](#2-\ud328-\uc3e0\ub9bc)\\n\\t- [2-1. \uc911\uc7a5\ud328](#2-1-\uc911\uc7a5\ud328)\\n\\t- [2-2. \ub178\ub450\ud328/\uadc0\uc871\ud328](#2-2-\ub178\ub450\ud328\uadc0\uc871\ud328)\\n\\t- [2-3. \uc790\ud328](#2-3-\uc790\ud328)\\n\\t- [2-4. \uc77c\uc0c9](#2-4-\uc77c\uc0c9)\\n\\t- [2-5. \ub3d9\uc218](#2-5-\ub3d9\uc218)\\n- [3. \ubab8\ud1b5 \ud615\ud0dc](#3-\ubab8\ud1b5-\ud615\ud0dc)\\n\\t- [3-1. \uc28c\ucbd4](#3-1-\uc28c\ucbd4)\\n\\t- [3-2. \ub610\uc774\ucbd4/\ucee4\ucbd4/\uae61\ucbd4](#3-2-\ub610\uc774\uce20\ucee4\ucbd4\uae61\ucbd4)\\n- [4. \uacb0\ub860](#4-\uacb0\ub860)\\n\\n\\n\\n## 1. \uba58\uc820 \uc5ec\ubd80\\n---\\n### 1-1. \uba58\uc820\\n---\\n\\n- \ud551\ud6c4(1/0)\\n- \uc774\ud398\ucf54(1/0)\\n- \uce58\ub610\uc774\uce20(2)\\n- \uae30\ud0c0\\n\\n:::info\\n\\n\uad04\ud638 \uc548\uc758 \uc22b\uc790\ub294 (\uba58\uc820 \uc2dc \ud310\uc218/\ud6c4\ub85c \uc2dc \ud310\uc218)\ub97c \uc758\ubbf8\ud568\\n:::\\n\\n### 1-2. \ud6c4\ub85c\\n---\\n\\n- \uc5ed\ud328(1)\\n- \ud0d5\uc57c\uc624(1)\\n- \ud63c\uc77c\uc0c9(3/2)\\n- \ub610\uc774\ub610\uc774(2)\\n- \uae30\ud0c0\\n\\n## 2. \ud328 \uc3e0\ub9bc\\n---\\n### 2-1. \uc911\uc7a5\ud328\\n---\\n\\n- \ud0d5\uc57c\uc624(1)\\n\\n:::tip\\n\\n\uc911\uc7a5\ud328\ub9cc\uc73c\ub85c \ud654\ub8cc\ud558\uae30\ub9cc \ud558\uba74 \ub418\ub294 \uc26c\uc6b4 \uc870\uac74\uc5d0, \ud6c4\ub85c\uae4c\uc9c0 \uac00\ub2a5\ud55c \ud0d5\uc57c\uc624\ub294 \ud0c0\uac00\uc758 \ub192\uc740 \ud654\ub8cc\ub97c \ube60\ub978 \uc18d\ub3c4\ub85c \uacac\uc81c\ud558\uae30\uc5d0 \ud6cc\ub96d\ud55c \uc218\ub2e8\uc774\ub2e4.\\n:::\\n\\n### 2-2. \ub178\ub450\ud328/\uadc0\uc871\ud328\\n---\\n\\n- X\\n- \uae30\ud0c0\\n\\t- \ucc64\ud0c0(\ud63c\uc804\ub300\uc694\uad6c)(2/1)\\n\\t- \uc900\ucc64\ud0c0(\uc21c\uc804\ub300\uc694\uad6c)(3/2)\\n\\t- \ud63c\ub178\ub450(2)\\n\\t- \uccad\ub178\ub450(\uc5ed\ub9cc)\\n\\t- \uad6d\uc0ac\ubb34\uc30d(\uc5ed\ub9cc)\\n\\n:::info\\n\\n\uae30\ud0c0\uc5d0 \uc18d\ud55c \ubaa9\ub85d\uc740 \uc0dd\ub7b5\ub418\uc9c0 \uc54a\uc740 \'\ub178\ub9ac\uae30 \uc5b4\ub824\uc6b4 \uc5ed\'\ub4e4\uc784.\\n:::\\n\\n:::tip\\n\\n\ub178\ub450\ud328\uac00 \ub9ce\ub2e4\uba74 \ube60\ub978 \ud654\ub8cc\uac00 \uc5b4\ub824\uc6b0\ub2c8 \ubaa8\ub450 \ubc84\ub9ac\uac70\ub098, \uc218\ube44\ub97c \uc5fc\ub450\ud558\uba70 \ud310\uc218 \ub192\uc774\uae30\uc5d0 \uc8fc\ub825\ud558\uc790.\\n:::\\n\\n:::tip\\n\\n\uad6d\uc0ac\ubb34\uc30d\uc740 \ubc30\ud328 \uc2dc \uadc0\uc871\ud328\uac00 10\uc885 \uc774\uc0c1\uc5d0 \uba38\ub9ac\uac00 \uc788\uac70\ub098, 11\uc885 \uc774\uc0c1\uc774 \uc544\ub2c8\ub77c\uba74 \ub418\ub3c4\ub85d \uc2dc\ub3c4\ud558\uc9c0 \ub9d0\uace0 9\uc8859\ud328\ub85c \uc720\uad6d\ud558\uc790.\\n:::\\n\\n### 2-3. \uc790\ud328\\n---\\n\\n- \uc5ed\ud328(1)\\n- \uae30\ud0c0\\n\\t- \uc18c\uc0bc\uc6d0(2+2)\\n\\t- \ub300\uc0bc\uc6d0(\uc5ed\ub9cc)\\n\\t- \uc18c\uc0ac\ud76c(\uc5ed\ub9cc)\\n\\t- \uc790\uc77c\uc0c9(\uc5ed\ub9cc)\\n\\n:::tip\\n\\n\uc5ed\ud328 \uc911\uc5d0\uc11c\ub3c4 \ub354\ube14\ub3d9\uc740 2\ud310\uc5ed\uc5d0 \uc624\uc57c\uc758 \ub192\uc740 \uc810\uc218 \ud310\uc815\uc73c\ub85c \uac00\uc131\ube44\uac00 \uc88b\uc740 \uc5ed \uc911 \ud558\ub098\uc774\ubbc0\ub85c \ub3d9\uc7a5 \uc624\uc57c\uc77c \ub54c\ub294 \ub3d9\uc744 \uaf2d \ucc59\uae30\ub3c4\ub85d \ud558\uc790.\\n:::\\n\\n### 2-4. \uc77c\uc0c9\\n---\\n\\n- \ud63c\uc77c\uc0c9(3/2)\\n- \uae30\ud0c0\\n\\t- \uccad\uc77c\uc0c9(6/5)\\n\\t- \uc77c\uae30\ud1b5\uad00(2/1)\\n\\t- \ub179\uc77c\uc0c9(\uc5ed\ub9cc)\\n\\t- \uad6c\ub828\ubcf4\ub4f1(\uc5ed\ub9cc)\\n\\n:::tip\\n\\n\ud63c\uc77c\uc0c9\uc740 \uac00\uc131\ube44\uac00 \uc88b\uc740 \uc5ed\uc774\ub2e4.\\n:::\\n\\n### 2-5. \ub3d9\uc218\\n---\\n\\n-  X\\n- \uae30\ud0c0\\n\\t- \uc0bc\uc0c9\ub3d9\uc21c(2/1)\\n\\t- \uc0bc\uc0c9\ub3d9\uac01(2)\\n\\n:::tip\\n\\n\uac00\uc131\ube44\uac00 \uc548\uc88b\ub2e4. \uc870\ud328 \uc2dc \uace0\ub824\ub294 \ucd5c\uc18c\ud55c\uc73c\ub85c\ub9cc \ud558\uc790.\\n:::\\n\\n\\n\\n## 3. \ubab8\ud1b5 \ud615\ud0dc\\n---\\n### 3-1. \uc28c\ucbd4\\n---\\n\\n- \ud551\ud6c4(1/0)\\n- \uc774\ud398\ucf54(1/0)\\n- \uae30\ud0c0\\n\\t- \uc0bc\uc0c9\ub3d9\uc21c(2/1)\\n\\t- \uc77c\uae30\ud1b5\uad00(2/1)\\n\\t- \ub7c9\ud398\ucf54(3/0)\\n\\n:::tip\\n\\n\ud3c9\ud654\ub77c\ub294 \ub73b\uc758 \ud551\ud6c4\ub294 \ubaa8\ub4e0 \uc5ed\uc744 \ud1b5\ud2c0\uc5b4 \uac00\uc7a5 \uc810\uc218\uac00 \uc791\uc740 \uc5ed\uc774\ub2e4.\\n\uadf8\ub7ec\ub2c8 \uc810\uc218\uc758 \uc7a0\uc7ac\ub825\uc774 \ub192\uc740 \ub9ac\uce58\ub97c \ub418\ub3c4\ub85d \uac19\uc774 \uc120\uc5b8\ud558\ub3c4\ub85d \ud558\uc790.\\n:::\\n\\n### 3-2. \ub610\uc774\uce20/\ucee4\ucbd4/\uae61\ucbd4\\n---\\n\\n- \uce58\ub610\uc774\uce20(2)\\n- \ub610\uc774\ub610\uc774(2)\\n- \uae30\ud0c0\\n\\t- \uc0bc\uc554\uac01(2)\\n\\t- \uc0ac\uc554\uac01(\uc5ed\ub9cc)\\n\\t- \uc0bc\uc0c9\ub3d9\uac01(2)\\n\\t- \ud63c\ub178\ub450(2)\\n\\t- \uccad\ub178\ub450(\uc5ed\ub9cc)\\n\\t- \uc0b0\uae61\ucbd4(2)\\n\\t- \uc2a4\uae61\ucbd4(\uc5ed\ub9cc)\\n\\n:::tip\\n\\n\uce58\ub610\uc774\uce20\ubcf4\ub2e4 \ud6c4\ub85c\uac00 \uac00\ub2a5\ud55c \ub610\uc774\ub610\uc774\uac00 \ucd9c\ud604\ud655\ub960\uc740 \ub354 \ub192\ub2e4.\\n\ud558\uc9c0\ub9cc, \uc218\ube44\uc758 \uc5b4\ub824\uc6c0\uacfc \ub354\ubd88\uc5b4 \ub9ac\uce58, \uba58\uc820\ucbd4\ubaa8, \uc77c\ubc1c, \ub4b7\ub3c4\ub77c\ub97c \ud1b5\ud574 \uace0\ub4dd\uc810\uc744 \ub178\ub9b4 \uc218 \uc5c6\ub2e4\ub294 \ub2e8\uc810 \ub54c\ubb38\uc5d0 \ub458 \uc911 \uc120\ud0dd\ud55c\ub2e4\uba74 \uce58\ub610\uc774\uce20\ub97c \uc6b0\uc120\uc73c\ub85c \ud558\uc790.\\n:::\\n\\n:::tip\\n\\n\ubc30\ud328 \uc2dc \ub610\uc774\uce20\uac00 4\uac1c \uc774\uc0c1\uc774 \uc544\ub2c8\ub77c\uba74 \uce58\ub610\uc774\uce20\ub098 \ub610\uc774\ub610\uc774\ub97c \ub178\ub9ac\uae30 \ubcf4\ub2e8 \uba38\ub9ac\ub97c \uae68\ub3c4\ub85d \ud558\uc790.\\n:::\\n\\n\\n\\n## 4. \uacb0\ub860\\n---\\n\\n- \uba58\uc820 \uc5ec\ubd80\\n\\n|    \uba58\uc820     |    \ud6c4\ub85c     |\\n|:-----------:|:-----------:| \\n|  \ud551\ud6c4(1/0)  |   \uc5ed\ud328(1)   |\\n| \uc774\ud398\ucf54(1/0) |  \ud0d5\uc57c\uc624(1)  |\\n| \uce58\ub610\uc774\uce20(2) | \ud63c\uc77c\uc0c9(3/2) |\\n|             | \ub610\uc774\ub610\uc774(2) |\\n\\n:::note Conclusion\\n\\n\uc5ed\ud328 \ud639\uc740 \ud0d5\uc57c\uc624\ub97c \ub178\ub9b4 \uc218 \uc788\uc73c\uba70, 3\ud310 \uc774\uc0c1\uc774 \ud655\uc815\uc778 \ubc30\ud328 \ucd08\ubc18,\\n\ud639\uc740 \ud6c4\ub85c \uc2dc\uc5d0\ub3c4 \uae30\ub300 \uc810\uc218\uac00 \ube44\uc2b7\ud55c\ub370 \ud654\ub8cc\uae4c\uc9c0 \uc5bc\ub9c8 \ub0a8\uc9c0 \uc54a\uc740 \uacbd\uc6b0\uc5d0 \ud6c4\ub85c\ud558\ub3c4\ub85d \ud558\uc790.\\n:::\\n\\n---\\n\\n- \ud328 \uc3e0\ub9bc\\n\\n|  \uc911\uc7a5\ud328   | \ub178\ub450\ud328 |  \uc790\ud328   |    \uc77c\uc0c9     | \ub3d9\uc218 |\\n|:---------:|:------:|:-------:|:-----------:|:----:|\\n| \ud0d5\uc57c\uc624(1) |   X    | \uc5ed\ud328(1) | \ud63c\uc77c\uc0c9(3/2) |  X   |\\n\\n:::note Conclusion\\n\\n\ubc30\ud328 \uc2dc \uc790\ud328\uac00 \ub9ce\uc73c\uba74 \uc5ed\ud328\uc640 \ud63c\uc77c\uc0c9\uc744, \uc801\uc73c\uba74 \ud0d5\uc57c\uc624\ub97c \ub178\ub824\ubcf4\uc790.\\n:::\\n\\n---\\n\\n- \ubab8\ud1b5 \ud615\ud0dc\\n\\n|    \uc28c\ucbd4     |   \ub610\uc774\uce20    |\\n|:-----------:|:-----------:|\\n|  \ud551\ud6c4(1/0)  | \uce58\ub610\uc774\uce20(2) |\\n| \uc774\ud398\ucf54(1/0) | \ub610\uc774\ub610\uc774(2) |\\n\\n:::note Conclusion\\n\\n\ub610\uc774\uce20\uac00 4\uac1c \uc774\uc0c1\uc774\uba74 \uce58\ub610\uc774\uce20\ub098 \ub610\uc774\ub610\uc774\ub97c, 3\uac1c \uc774\ud558\uba74 \uba38\ub9ac\ub97c \uae68\uace0 \ud551\ud6c4\ub97c \ub178\ub824\ubcf4\uc790."}]}')}}]);